import React, { Component } from "react";
import axios from "axios";

import Notification from "./Notification";
import Spinner from "./Spinner";

export default class App extends Component {
    state = {
        articles: [],
        error: false,
        loading: false,
    };

    componentDidMount() {
        this.setState({ loading: true });

        axios
            .get("http://hn.algolia.com/api/v10/search?query=covid")
            .then((response) => {
                console.log(response);
                this.setState({
                    articles: response.data.hits,
                    // loading: false,
                });
            })
            .catch((error) => {
                console.log(error);

                this.setState({
                    error,
                    // loading: false
                });
            })
            .finally(() => this.setState({ loading: false }));
    }

    render() {
        let { articles, loading, error } = this.state;

        let articlesItems = articles.map(({ objectID, title, url }) => (
            <li key={objectID}>
                <a href={url} target="_blank">
                    {title}
                </a>
            </li>
        ));

        return (
            <div>
                <h1>https://hn.algolia.com/api</h1>
                {loading && <Spinner message="Loading..." />}
                {error && <Notification message="" />}
                {articles.length > 0 && <ul>{articlesItems}</ul>}
            </div>
        );
    }
}
